externalEventHandlers.body:
function useNumberAxis() {
    options.axes = [
        {
            type: 'category',
            position: 'bottom'
        },
        {
            type: 'number',
            position: 'left',
            min: 1,
            label: { format: '.0f' },
            tick: { count: 10 }
        }
    ];

}

function useLogAxis() {
    options.axes = [
        {
            type: 'category',
            position: 'bottom'
        },
        {
            type: 'log',
            position: 'left',
            min: 10,
            label: { format: '.0f' },
            tick: { count: 10 }
        }
    ];

}

function useBaseTwoLogAxis() {
    options.axes = [
        {
            type: 'category',
            position: 'bottom'
        },
        {
            type: 'log',
            position: 'left',
            min: 10,
            label: { format: '.0f' },
            tick: { count: 10 },
            base: 2
        }
    ];

}

function useLogAxisWithFewerTicks() {
    options.axes = [
        {
            type: 'category',
            position: 'bottom'
        },
        {
            type: 'log',
            position: 'left',
            min: 10,
            label: { format: '.0f' },
            tick: { count: 2 }
        }
    ];

}
