utils:
var carMappings = {
    tyt: 'Toyota',
    frd: 'Ford',
    prs: 'Porsche',
    nss: 'Nissan',
};

var colourMappings = {
    cb: 'Cadet Blue',
    bw: 'Burlywood',
    fg: 'Forest Green',
};

function colourCellRenderer(params) {
    if (params.value === '(Select All)') {
        return params.value;
    }
    return ('<span style="color: ' +
        removeSpaces(params.valueFormatted) +
        '">' +
        params.valueFormatted +
        '</span>');
}

function currencyFormatter(params) {
    var value = Math.floor(params.value);
    if (isNaN(value)) {
        return '';
    }
    return 'Â£' + value.toString().replace(/(\d)(?=(\d{3})+(?!\d))/g, '$1,');
}

function numberValueSetter(params) {
    if (isNaN(parseFloat(params.newValue)) || !isFinite(params.newValue)) {
        return false; // don't set invalid numbers!
    }
    params.data.price = params.newValue;
    return true;
}

function removeSpaces(str) {
    return str ? str.replace(/\s/g, '') : str;
}
